        &::placeholder{
          font-size: 14px;
          line-height: 24px;
          color: rgba(31, 32, 65, 0.25);
        }
      }

      &-info{
        font-size: 14px;
        line-height: 24px;
        color: rgba(31, 32, 65, 0.75);
        margin-bottom: 20px;
      }
    }
  }

  &-inner{
    display: flex;
    justify-content: space-between;
  }

  &__info{
    max-width: 260px;

    &_block{
      font-size: 14px;
      line-height: 24px;
      color: rgba(31, 32, 65, 0.75);
    }
  }

  &__logo{
     margin-bottom: 20px;
  }

  &__title{
    margin-bottom: 21px;
    font-style: normal;
    font-weight: 700;
    font-size: 12px;
    line-height: 15px;
    text-transform: uppercase;
    color: #1F2041;
  }

  &__list{

    &_item{
      margin-bottom: 20px;
    }

    &_link{
      font-size: 14px;
      line-height: 17px;
      color: rgba(31, 32, 65, 0.5);

      &:hover{
        color: rgba(31, 32, 65, 0.75);
      }
    }
  }

  &__input{

    &-wrapper{
      background: #FFFFFF;
      border: 1px solid rgba(31, 32, 65, 0.25);
      box-sizing: border-box;
      border-radius: 4px;
      width: 100%;
      display: flex;
    }
  }

  &__button{

    &-input{
      padding-right: 9px;
      background-color: transparent;
      border: none;
      cursor: pointer;
      outline: none;
    }
  }

  /*
    footer__copy
  */

  &__copy{
    padding: 23px 0;

    &-inner{
      display: flex;
      justify-content: space-between;
    }

    &-text{
      font-size: 14px;
      line-height: 17px;
      color: rgba(31, 32, 65, 0.75);
    }

    &_link{

      &-social{
        display: block;
        margin-right: 20px;

        &:last-child{
          margin-right: 0;
        }
      }
    }
  }

}

.social-wrapper{
  display: flex;
}

