@import url('./../default.scss');
@import url('./../../blocks/header/header.scss');
@import url('./../../blocks/footer/footer.scss');
@import url('./../../blocks/header/menu/menu-list.scss');
@import url('./../../blocks/cards.scss');
@import url('./../../blocks/likes/likes.scss');

.material-icons{
  font-family: 'Material Icons';
  font-style: normal;
  font-weight: 400;
  font-size: 24px;
  line-height: 20px;
  display: flex;
  align-items: center;
  text-align: center;
  background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  // text-fill-color: transparent;
}

.content{
  display: flex;
  height: 485px;
  width: 100%;

  &__box{
    height: 100%;
    display: flex;
    flex-direction: column;

    &-1{
      width: 65%;
    }

    &-2{
      width: 35%;
    }
  }
}
.img-1{
  height: 100%;
}
.img-2,
.img-3{
  height: 50%;
}
.info{
  padding: 70px 0;
  border-bottom: 1px solid rgba(31, 32, 65, 0.25);

  &-inner{
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  &__box{
    
    &-1{
      width: 60%;
    }

    &-2{
      width: 35%;
    }

  }
}

.information{
  width: 291px;

  &__icon{
    width: 48px;
    height: 48px;
    margin-right: 10px;
  }

  &__item{
    display: flex;
    padding-top: 20px;
    padding-bottom: 17px;
    border-bottom: 1px solid rgba(31, 32, 65, 0.1);

    &:last-child{
      border: none;
    }
  }
   
  &__title{
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 700;
    font-size: 14px;
    line-height: 24px;
    color: rgba(31, 32, 65, 0.75);
  }

  &__text{
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 400;
    font-size: 14px;
    line-height: 24px;
    color: rgba(31, 32, 65, 0.75);
  }
}

.impression{
  
  &__title{
    margin-bottom: 20px;
  }

  &-inner{
    display: flex;
    justify-content: space-between;
  }

  // &__diagram{

  // }
  // &__descr{

  // }

  &__list{

    &_item{
      font-family: 'Montserrat';
      font-style: normal;
      font-weight: 400;
      font-size: 14px;
      line-height: 24px;
      color: rgba(31, 32, 65, 0.75);
      position: relative;

      &::after{
        content: '';
        position: absolute;
        left: -15px;
        top: 50%;
        transform: translateY(-50%);
        width: 10px;
        height: 10px;
        background-color: #000;
        border-radius: 100%;
      }

      &-1{

        &::after{
          background: linear-gradient(180deg, #FFE39C 0%, #FFBA9C 100%);
        }
      }

      &-2{

        &::after{
          background: linear-gradient(180deg, #6FCF97 0%, #66D2EA 100%);
        }
      }

      &-3{

        &::after{
          background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
        }
      }

      &-4{

        &::after{
          background: linear-gradient(180deg, #919191 0%, #3D4975 100%);
        }
      }
    }
  }

}

.semicircle{
  width: 59px;
  height: 120px;
  border: 4px solid #000;
  border-radius: 100% 0 0 100% / 50% 0 0 50%;
}

.reviews{

  &__title{
    display: flex;
    justify-content: space-between;

    &-info{
      font-family: 'Montserrat';
      font-style: normal;
      font-weight: 400;
      font-size: 14px;
      line-height: 14px;
      text-align: right;
      color: rgba(31, 32, 65, 0.5);
    }
  }
}

.review{

  &__name{
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 700;
    font-size: 14px;
    line-height: 24px;
    color: rgba(31, 32, 65, 0.75);
  }

  &__date{
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 400;
    font-size: 14px;
    line-height: 24px;
    color: rgba(31, 32, 65, 0.75);
  }

  &__avatar{
    width: 48px;
    height: 48px;
    border: 2px solid #FFFFFF;
    box-sizing: border-box;
    filter: drop-shadow(0px 10px 20px rgba(31, 32, 65, 0.1));
    border-radius: 32px;

    &_image{
      width: 100%;
      height: 100%;
      border-radius: 100%;
    }
  }
}

.regulations{

  &__title{
    margin-bottom: 20px;
  }

  &__list{

    &_item{
      position: relative;
      margin-bottom: 10px;

      &:last-child{
        margin-bottom: 0;
      }

      &::before{
        content: '';
        position: absolute;
        left: -20px;
        top: 50%;
        transform: translateY(-50%);
        width: 10px;
        height: 10px;
        border-radius: 100%;
        background: rgba(31, 32, 65, 0.25);
      }
    }
  } 
}

.cancellation{
  width: 341px;

  &__title{
    margin-bottom: 20px;
  }

  &__text{
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 400;
    font-size: 14px;
    line-height: 24px;
    color: rgba(31, 32, 65, 0.75);
  }
}
